// Simple validation schemasconst validate = simplyValid({
    schema: 'hasValue'
  });

  validate('test'); // => { isValid: true }
  validate(''); // => { isValid: false, story: [{ test: 'hasValue', value: '' }] }
  simplyValid({
    schema: 'hasValue'
  }, 'test'); // => { isValid: true }// Array Validation Schemasconst validate = simplyValid({ schema: ['hasValue', 'hasNumber']});validate('test1123'); // => { isValid: true }validate('test'); // => { isValid: false, story: [{ test: 'hasNumbers', value: 'test' }] }// Object Validation Schemaconst validate = simplyValid({ schema: {   test: ['hasNumbers', 'hasLetters'],   thing: 'hasValue',   other: {     nestedThing: ['isPositive', 'hasNumbers']   } }});validate({  test: 'cool112',  thing: 'test',  other: {    nestedThing: '1234'  }}); // => { isValid: true }
